import React from 'react';
import { Toast } from 'antd-mobile';
import Fetch from '../../components/fetch';


export default class SelectCity extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            inputVal: '',
            inputFocus: false,
            curCity: '',
            cityList: [],
            searchCity: [],
            citySort: {
                isHot: {
                    key: '热门',
                    arr: []
                },
                A: {
                    key: 'A',
                    arr: []
                },
                B: {
                    key: 'B',
                    arr: []
                },
                C: {
                    key: 'C',
                    arr: []
                },
                D: {
                    key: 'D',
                    arr: []
                },
                E: {
                    key: 'E',
                    arr: []
                },
                F: {
                    key: 'F',
                    arr: []
                },
                G: {
                    key: 'G',
                    arr: []
                },
                H: {
                    key: 'H',
                    arr: []
                },
                J: {
                    key: 'J',
                    arr: []
                },
                K: {
                    key: 'K',
                    arr: []
                },
                L: {
                    key: 'L',
                    arr: []
                },
                M: {
                    key: 'M',
                    arr: []
                },
                N: {
                    key: 'N',
                    arr: []
                },
                P: {
                    key: 'P',
                    arr: []
                },
                Q: {
                    key: 'Q',
                    arr: []
                },
                R: {
                    key: 'R',
                    arr: []
                },
                S: {
                    key: 'S',
                    arr: []
                },
                T: {
                    key: 'T',
                    arr: []
                },
                W: {
                    key: 'W',
                    arr: []
                },
                X: {
                    key: 'X',
                    arr: []
                },
                Y: {
                    key: 'Y',
                    arr: []
                },
                Z: {
                    key: 'Z',
                    arr: []
                }
            }
        }
    }

    componentWillMount() {
        document.body.scrollIntoView();
        document.title = '选择城市';
    }

    componentDidMount() {
        Toast.loading('正在加载');
        Fetch.post('/d-app/API/region/findRegionList', {
            parentId: 0,
            regionType: 3
        }).then(res => {
            Toast.hide();
            console.log(res);
            if (res) {
                let cityArr = res;
                cityArr.forEach((x, i) => {
                    this.state.citySort[this._alphabet(x.regionName)[0][0]].arr.push(x.regionName);
                    if(x.isHot === 1){
                        this.state.citySort.isHot.arr.push(x.regionName)
                    }
                })

                this.setState({
                    citySort: this.state.citySort,
                    cityList: res
                })
            }
        }).catch(err => {
            console.log(err);
        })
    }

    onChangeVal(value) {
        if(value.length < 20){
            const arr = [];
            this.state.cityList.forEach((item) => {
                if (item.regionName.indexOf(value) !== -1) {
                    arr.push(item.regionName)
                }
            })
            this.setState({
                inputVal: value,
                searchCity: arr
            })
        }
    }

    onFocusInput(key, focus) {

        if (key) {
            this.setState({
                inputFocus: true
            })
        } else if (!key && this.state.inputVal === '') {
            this.setState({
                inputFocus: false
            })
        }
    }
    //滚动
    onScrollBody(key) {
        const scrollHeight = document.querySelector('.city-item-' + key).offsetTop;
        window.scrollTo(0, scrollHeight);

        this.setState({
            curCity: key
        })
        setTimeout(() => {
            this.setState({
                curCity: ''
            })
        }, 300);
    }

    onClickCity(str) {

        if(localStorage.business){
            const business = JSON.parse(localStorage.business);
            business.curCity = str;
            localStorage.setItem('business', JSON.stringify(business));
        } else {
            const business = {
                curCity: str
            }
            localStorage.setItem('business', JSON.stringify(business));
        }

        this.props.history.goBack();
    }

    // 中文转字母
    _alphabet(str) {
        let strChineseFirstPY = "";
        let oMultiDiff = { "19969": "DZ", "19975": "WM", "19988": "QJ", "20048": "YL", "20056": "SC", "20060": "NM", "20094": "QG", "20127": "QJ", "20167": "QC", "20193": "YG", "20250": "KH", "20256": "ZC", "20282": "SC", "20285": "QJG", "20291": "TD", "20314": "YD", "20340": "NE", "20375": "TD", "20389": "YJ", "20391": "CZ", "20415": "PB", "20446": "YS", "20447": "SQ", "20504": "TC", "20608": "KG", "20854": "QJ", "20857": "ZC", "20911": "PF", "20504": "TC", "20608": "KG", "20854": "QJ", "20857": "ZC", "20911": "PF", "20985": "AW", "21032": "PB", "21048": "XQ", "21049": "SC", "21089": "YS", "21119": "JC", "21242": "SB", "21273": "SC", "21305": "YP", "21306": "QO", "21330": "ZC", "21333": "SDC", "21345": "QK", "21378": "CA", "21397": "SC", "21414": "XS", "21442": "SC", "21477": "JG", "21480": "TD", "21484": "ZS", "21494": "YX", "21505": "YX", "21512": "HG", "21523": "XH", "21537": "PB", "21542": "PF", "21549": "KH", "21571": "E", "21574": "DA", "21588": "TD", "21589": "O", "21618": "ZC", "21621": "KHA", "21632": "ZJ", "21654": "KG", "21679": "LKG", "21683": "KH", "21710": "A", "21719": "YH", "21734": "WOE", "21769": "A", "21780": "WN", "21804": "XH", "21834": "A", "21899": "ZD", "21903": "RN", "21908": "WO", "21939": "ZC", "21956": "SA", "21964": "YA", "21970": "TD", "22003": "A", "22031": "JG", "22040": "XS", "22060": "ZC", "22066": "ZC", "22079": "MH", "22129": "XJ", "22179": "XA", "22237": "NJ", "22244": "TD", "22280": "JQ", "22300": "YH", "22313": "XW", "22331": "YQ", "22343": "YJ", "22351": "PH", "22395": "DC", "22412": "TD", "22484": "PB", "22500": "PB", "22534": "ZD", "22549": "DH", "22561": "PB", "22612": "TD", "22771": "KQ", "22831": "HB", "22841": "JG", "22855": "QJ", "22865": "XQ", "23013": "ML", "23081": "WM", "23487": "SX", "23558": "QJ", "23561": "YW", "23586": "YW", "23614": "YW", "23615": "SN", "23631": "PB", "23646": "ZS", "23663": "ZT", "23673": "YG", "23762": "TD", "23769": "ZS", "23780": "QJ", "23884": "QK", "24055": "XH", "24113": "DC", "24162": "ZC", "24191": "GA", "24273": "QJ", "24324": "NL", "24377": "TD", "24378": "QJ", "24439": "PF", "24554": "ZS", "24683": "TD", "24694": "WE", "24733": "LK", "24925": "TN", "25094": "ZG", "25100": "XQ", "25103": "XH", "25153": "PB", "25170": "PB", "25179": "KG", "25203": "PB", "25240": "ZS", "25282": "FB", "25303": "NA", "25324": "KG", "25341": "ZY", "25373": "WZ", "25375": "XJ", "25384": "A", "25457": "A", "25528": "SD", "25530": "SC", "25552": "TD", "25774": "ZC", "25874": "ZC", "26044": "YW", "26080": "WM", "26292": "PB", "26333": "PB", "26355": "ZY", "26366": "CZ", "26397": "ZC", "26399": "QJ", "26415": "ZS", "26451": "SB", "26526": "ZC", "26552": "JG", "26561": "TD", "26588": "JG", "26597": "CZ", "26629": "ZS", "26638": "YL", "26646": "XQ", "26653": "KG", "26657": "XJ", "26727": "HG", "26894": "ZC", "26937": "ZS", "26946": "ZC", "26999": "KJ", "27099": "KJ", "27449": "YQ", "27481": "XS", "27542": "ZS", "27663": "ZS", "27748": "TS", "27784": "SC", "27788": "ZD", "27795": "TD", "27812": "O", "27850": "PB", "27852": "MB", "27895": "SL", "27898": "PL", "27973": "QJ", "27981": "KH", "27986": "HX", "27994": "XJ", "28044": "YC", "28065": "WG", "28177": "SM", "28267": "QJ", "28291": "KH", "28337": "ZQ", "28463": "TL", "28548": "DC", "28601": "TD", "28689": "PB", "28805": "JG", "28820": "QG", "28846": "PB", "28952": "TD", "28975": "ZC", "29100": "A", "29325": "QJ", "29575": "SL", "29602": "FB", "30010": "TD", "30044": "CX", "30058": "PF", "30091": "YSP", "30111": "YN", "30229": "XJ", "30427": "SC", "30465": "SX", "30631": "YQ", "30655": "QJ", "30684": "QJG", "30707": "SD", "30729": "XH", "30796": "LG", "30917": "PB", "31074": "NM", "31085": "JZ", "31109": "SC", "31181": "ZC", "31192": "MLB", "31293": "JQ", "31400": "YX", "31584": "YJ", "31896": "ZN", "31909": "ZY", "31995": "XJ", "32321": "PF", "32327": "ZY", "32418": "HG", "32420": "XQ", "32421": "HG", "32438": "LG", "32473": "GJ", "32488": "TD", "32521": "QJ", "32527": "PB", "32562": "ZSQ", "32564": "JZ", "32735": "ZD", "32793": "PB", "33071": "PF", "33098": "XL", "33100": "YA", "33152": "PB", "33261": "CX", "33324": "BP", "33333": "TD", "33406": "YA", "33426": "WM", "33432": "PB", "33445": "JG", "33486": "ZN", "33493": "TS", "33507": "QJ", "33540": "QJ", "33544": "ZC", "33564": "XQ", "33617": "YT", "33632": "QJ", "33636": "XH", "33637": "YX", "33694": "WG", "33705": "PF", "33728": "YW", "33882": "SR", "34067": "WM", "34074": "YW", "34121": "QJ", "34255": "ZC", "34259": "XL", "34425": "JH", "34430": "XH", "34485": "KH", "34503": "YS", "34532": "HG", "34552": "XS", "34558": "YE", "34593": "ZL", "34660": "YQ", "34892": "XH", "34928": "SC", "34999": "QJ", "35048": "PB", "35059": "SC", "35098": "ZC", "35203": "TQ", "35265": "JX", "35299": "JX", "35782": "SZ", "35828": "YS", "35830": "E", "35843": "TD", "35895": "YG", "35977": "MH", "36158": "JG", "36228": "QJ", "36426": "XQ", "36466": "DC", "36710": "JC", "36711": "ZYG", "36767": "PB", "36866": "SK", "36951": "YW", "37034": "YX", "37063": "XH", "37218": "ZC", "37325": "ZC", "38063": "PB", "38079": "TD", "38085": "QY", "38107": "DC", "38116": "TD", "38123": "YD", "38224": "HG", "38241": "XTC", "38271": "ZC", "38415": "YE", "38426": "KH", "38461": "YD", "38463": "AE", "38466": "PB", "38477": "XJ", "38518": "YT", "38551": "WK", "38585": "ZC", "38704": "XS", "38739": "LJ", "38761": "GJ", "38808": "SQ", "39048": "JG", "39049": "XJ", "39052": "HG", "39076": "CZ", "39271": "XT", "39534": "TD", "39552": "TD", "39584": "PB", "39647": "SB", "39730": "LG", "39748": "TPB", "40109": "ZQ", "40479": "ND", "40516": "HG", "40536": "HG", "40583": "QJ", "40765": "YQ", "40784": "QJ", "40840": "YK", "40863": "QJG" };

        let arrResult = new Array(); //保存中间结果的数组
        for (let i = 0, len = str.length; i < len; i++) {
            //获得unicode码
            let ch = str.charAt(i);
            //检查该unicode码是否在处理范围之内,在则返回该码对映汉字的拼音首字母,不在则调用其它函数处理
            arrResult.push(checkCh(ch));
        }

        //处理arrResult,返回所有可能的拼音首字母串数组
        return mkRslt(arrResult);

        function checkCh(ch) {
            let uni = ch.charCodeAt(0);
            //如果不在汉字处理范围之内,返回原字符,也可以调用自己的处理函数
            if (uni > 40869 || uni < 19968)
                return ch; //dealWithOthers(ch);
            //检查是否是多音字,是按多音字处理,不是就直接在strChineseFirstPY字符串中找对应的首字母
            return (oMultiDiff[uni] ? oMultiDiff[uni] : (strChineseFirstPY.charAt(uni - 19968)));
        }

        function mkRslt(arr) {
            let arrRslt = [""];
            for (let i = 0, len = arr.length; i < len; i++) {
                let str = arr[i];
                let strlen = str.length;
                if (strlen === 1) {
                    for (let k = 0; k < arrRslt.length; k++) {
                        arrRslt[k] += str;
                    }
                } else {
                    let tmpArr = arrRslt.slice(0);
                    arrRslt = [];
                    for (let k = 0; k < strlen; k++) {
                        //复制一个相同的arrRslt
                        let tmp = tmpArr.slice(0);
                        //把当前字符str[k]添加到每个元素末尾
                        for (let j = 0; j < tmp.length; j++) {
                            tmp[j] += str.charAt(k);
                        }
                        //把复制并修改后的数组连接到arrRslt上
                        arrRslt = arrRslt.concat(tmp);
                    }
                }
            }
            return arrRslt;
        }
    }


    _citySort() {
        const citySort = this.state.citySort;
        const cityKeyArr = [];
        for (let item in this.state.citySort) {
            if (citySort[item].arr.length > 0) {
                cityKeyArr.push(
                    <a
                        style={{ textAlign: 'left', lineHeight: '0.8rem', fontSize: '0.6rem', color: '#FC703B', flex: 1, display: 'block' }}
                        key={item}
                        name={`${citySort[item].key}`}
                        onClick={() => { this.onScrollBody(citySort[item].key) }}
                    >
                        {item.key}
                    </a>
                )
            }
        }
        console.log(cityKeyArr)
        return cityKeyArr
    }


    render() {
        const { inputFocus, inputVal, citySort } = this.state;
        return (
            <div
                className="container-selectCity"
            >

                <div className="header box-sizing">
                    <div className="search-input-wrap"
                        style={{ width: '17.25rem' }}
                    >
                        <div
                            className={inputFocus ? "input-tit focus" : 'input-tit'}
                            onClick={() => this.onFocusInput(true, 'focus')}
                        >
                            <i className="iconfont icon-sousuo"></i>
                            <span>{inputFocus ? '' : '输入城市名'}</span>
                        </div>
                        <input
                            onChange={(e) => this.onChangeVal(e.target.value)}
                            onFocus={() => this.onFocusInput(true)}
                            onBlur={() => this.onFocusInput(false)}
                            value={inputVal}
                        />
                    </div>
                    {/* 显示当前字母 */}
                    <div
                        className={'flex-center curCity '}
                        style={{
                            width: '2.5rem', height: '2.5rem', position: 'fixed', background: 'rgba(0,0,0,0.5)',
                            color: '#fff', fontSize: '1rem', top: '50%', left: '50%',
                            margin: '-1.25rem 0 0 -1.25rem', display: this.state.curCity === '' ? 'none' : 'flex',
                            borderRadius: '0.5rem'
                        }}

                    >
                        <span
                            style={{ width: '100%', height: '1.5rem', fontSize: '0.9rem', lineHeight: '1.5rem', textAlign: 'center', marginLeft: '-0.6rem', display: 'block' }}
                        >
                            {this.state.curCity}
                        </span>
                    </div>
                    {/* 右边定位列表 */}
                    <div
                        className="flex flex-align-items flex-space-between"
                        style={{ width: '2rem', position: 'fixed', top: '5rem', bottom: '3rem', right: '0rem', flexDirection: 'column' }}
                    >
                        {
                            Object.keys(citySort).map((item, index) => {
                                if (citySort[item].arr.length > 0) {
                                    return (
                                        <a
                                            style={{ 
                                                textAlign: 'left', lineHeight: '0.8rem', fontSize: '0.6rem', 
                                                color: '#FC703B', flex: 1, display: 'block' 
                                            }}
                                            key={index}
                                            name={`${citySort[item].key}`}
                                            onClick={() => { this.onScrollBody(citySort[item].key) }}
                                        >
                                            {citySort[item].key}
                                        </a>
                                    )
                                }
                            })
                        }

                    </div>
                    {/* 城市列表 */}
                </div>
                {
                    this.state.inputFocus && this.state.inputVal !== '' ?

                        this.state.searchCity.map((item, index) => (
                            <div
                                style={{
                                    height: '2.2rem', width: '100%', background: '#fff', borderBottom: '1px solid rgba(247, 247, 247, 1)', padding: '0 1rem', lineHeight: '2.2rem'
                                }}
                                key={index}
                                onClick={() => this.onClickCity(item) }
                            >
                                {item}
                            </div>
                        ))
                        :
                        Object.keys(citySort).map((item, index) => (
                            <a
                                key={index}
                                className="box-sizing"
                                style={{ width: '100%', height: 'auto', display: 'block' }}
                                id={citySort[item].key}
                            >   {
                                    citySort[item].arr.length > 0 ?
                                        <div
                                            className={`city-item-${citySort[item].key}`}
                                            style={{ height: '1.8rem', lineHeight: '1.8rem', color: '#444', borderBottom: '1px solid rgba(247, 247, 247, 1)', textIndent: '1rem' }}
                                        >{citySort[item].key}</div> : null
                                }
                                {
                                    citySort[item].arr.map((x, key) => (
                                        <div
                                            className="flex box-sizing flex-align-items"
                                            style={{ height: '2.2rem', width: '100%', background: '#fff', borderBottom: '1px solid rgba(247, 247, 247, 1)', padding: '0 1rem' }}
                                            key={key}
                                            onClick={() => this.onClickCity(x)}
                                        >
                                            {x}
                                        </div>
                                    ))
                                }

                            </a>
                        ))
                }
            </div>
        )
    }
}